xwikidoc.content:
{{velocity wiki="true"}}
  ##
  ## XWiki Dashboard
  ##
  ## This dashboard can be used for both wikis (Main.WebHome) and spaces (*.WebHome).
  ##
  #if ($doc.space != "Main")
    #set ($isSpaceDashboard = true)
  #else
    #set ($isSpaceDashboard = false)
  #end
  {{dashboard}}
    (((
    #if (!$isSpaceDashboard)
      {{spaces/}}

      {{tagcloud/}}
    #else
      {{spaceindex space="$doc.space"/}}

      {{tagcloud  space="$doc.space"/}}
    #end
    )))
    (((
    #if ($isSpaceDashboard)
      {{activity spaces="$doc.space"/}}
    #else
      {{activity/}}
    #end
    )))
  {{/dashboard}}
{{/velocity}}
