{{/velocity}}

{{html}}
<table id="tableid" class="grid sortable filterable doOddEven">
  <tr class="sortHeader">
    <th>Überschrift1</th>
    <th>Überschrift2</th>
  </tr>
  <tr>
   <td>Zelle11</td>
   <td>Zelle12</td>
  </tr>
  <tr>
   <td>Zelle21</td>
   <td>Zelle22</td>
  </tr>
</table>
{{/html}}
)))

